import numpy as np
from scipy.optimize import minimize, differential_evolution

def construct_packing():
    """
    Packs 26 circles into a [0,1]x[0,1] square to maximize the sum of radii.
    Uses multiple different structural approaches.
    """
    n = 26

    def create_bilayer_structure():
        """Create a bilayer structure: two offset hex lattices."""
        centers = []

        # Layer 1: 13 circles in hex pattern
        for i in range(4):
            for j in range(4):
                if len(centers) >= 13:
                    break
                x = 0.15 + j * 0.23
                y = 0.15 + i * 0.23 + (j % 2) * 0.115
                centers.append([x, y])

        # Layer 2: 13 circles offset
        for i in range(4):
            for j in range(4):
                if len(centers) >= n:
                    break
                x = 0.265 + j * 0.23
                y = 0.265 + i * 0.23 + (j % 2) * 0.115
                centers.append([x, y])

        return np.array(centers[:n])

    def create_border_interior():
        """Create border + interior structure."""
        centers = []

        # Border: 16 circles around perimeter
        for i in range(5):
            centers.append([0.12 + i * 0.19, 0.12])  # Bottom
            if i < 4:
                centers.append([0.88, 0.12 + (i+1) * 0.19])  # Right
        for i in range(4):
            centers.append([0.88 - (i+1) * 0.19, 0.88])  # Top
            centers.append([0.12, 0.88 - (i+1) * 0.19])  # Left

        # Interior: 10 circles in optimal positions
        interior = [
            [0.35, 0.35], [0.65, 0.35], [0.35, 0.65], [0.65, 0.65],
            [0.5, 0.5], [0.5, 0.3], [0.5, 0.7], [0.3, 0.5], [0.7, 0.5],
            [0.42, 0.42]
        ]
        centers.extend(interior)

        return np.array(centers[:n])

    def create_dense_hex():
        """Create a denser hex pattern optimized for 26."""
        centers = []

        # 26 = 5 + 6 + 5 + 6 + 4 (alternating rows)
        rows_config = [5, 6, 5, 6, 4]
        y_spacing = 0.19

        for row_idx, num_in_row in enumerate(rows_config):
            y = 0.12 + row_idx * y_spacing
            x_spacing = 0.76 / (num_in_row - 1) if num_in_row > 1 else 0
            x_offset = 0.12 if num_in_row == 6 else 0.165

            for col in range(num_in_row):
                x = x_offset + col * x_spacing
                centers.append([x, y])

        return np.array(centers[:n])

    def objective(vars):
        return -np.sum(vars[2*n:])

    def constraints(vars):
        pts = vars[:2*n].reshape((n, 2))
        radii = vars[2*n:]
        cons = []

        # Boundary constraints
        for i in range(n):
            cons.extend([
                pts[i, 0] - radii[i],
                1.0 - pts[i, 0] - radii[i],
                pts[i, 1] - radii[i],
                1.0 - pts[i, 1] - radii[i],
                radii[i]
            ])

        # Non-overlap constraints
        for i in range(n):
            for j in range(i + 1, n):
                d = np.sqrt(np.sum((pts[i] - pts[j])**2))
                cons.append(d - (radii[i] + radii[j]))

        return np.array(cons)

    bounds = [(0, 1)] * (2 * n) + [(0, 0.5)] * n
    cons_dict = {'type': 'ineq', 'fun': constraints}

    best_result = None
    best_sum = 0

    # Try different structural approaches
    structures = [
        ("bilayer", create_bilayer_structure()),
        ("border_interior", create_border_interior()),
        ("dense_hex", create_dense_hex()),
    ]

    for name, centers in structures:
        # Try multiple radius initializations
        for init_r in [0.09, 0.095, 0.10, 0.105]:
            init_radii = np.full(n, init_r)
            x0 = np.concatenate([centers.flatten(), init_radii])

            # Multi-stage optimization
            res = minimize(
                objective, x0,
                method='SLSQP',
                bounds=bounds,
                constraints=cons_dict,
                options={'maxiter': 2000, 'ftol': 1e-13}
            )

            # Refine
            if res.success or res.fun < 0:
                for _ in range(2):
                    res2 = minimize(
                        objective, res.x,
                        method='SLSQP',
                        bounds=bounds,
                        constraints=cons_dict,
                        options={'maxiter': 1500, 'ftol': 1e-14}
                    )
                    if res2.fun < res.fun:
                        res = res2

            current_sum = -res.fun

            if current_sum > best_sum:
                best_sum = current_sum
                best_result = res.x

    # Try differential evolution on best result
    if best_result is not None and best_sum < 2.636:
        def de_objective(vars):
            cons = constraints(vars)
            if np.any(cons < -1e-6):
                return 1e10
            return -np.sum(vars[2*n:])

        # Use best result as seed
        result_de = differential_evolution(
            de_objective,
            bounds,
            seed=42,
            maxiter=500,
            atol=1e-10,
            tol=1e-10,
            workers=1,
            init='latinhypercube'
        )

        if -result_de.fun > best_sum:
            best_result = result_de.x
            best_sum = -result_de.fun

    # Final refinement
    if best_result is not None:
        for _ in range(3):
            res_final = minimize(
                objective, best_result,
                method='SLSQP',
                bounds=bounds,
                constraints=cons_dict,
                options={'maxiter': 3000, 'ftol': 1e-15}
            )

            if -res_final.fun > best_sum:
                best_result = res_final.x
                best_sum = -res_final.fun

    # Extract final solution
    final_centers = best_result[:2*n].reshape((n, 2))
    final_radii = best_result[2*n:]
    sum_r = np.sum(final_radii)

    return final_centers, final_radii, sum_r

def run_packing():
    return construct_packing()

