from scipy.spatial.distance import pdist

def construct_packing():
    num_circles = 26

    def get_fitness(params):
        return -np.sum(params[2*num_circles:])

    def get_centers_radii(params):
        centers = params[:2*num_circles].reshape((num_circles, 2))
        radii = params[2*num_circles:]
        return centers, radii

    def check_feasibility(params, tol=1e-9):
        centers, radii = get_centers_radii(params)
        # Check bounds
        if np.any(radii < -tol):
            return False
        if np.any(centers - radii.reshape(-1,1) < -tol):
            return False
        if np.any(centers + radii.reshape(-1,1) > 1 + tol):
            return False
        # Check overlaps
        for i in range(num_circles):
            for j in range(i+1, num_circles):
                dist = np.linalg.norm(centers[i] - centers[j])
                if dist < radii[i] + radii[j] - tol:
                    return False
        return True

    def constraints_ineq(p):
        centers, radii = get_centers_radii(p)
        cons = []
        # Inside square
        for i in range(num_circles):
            cons.append(centers[i, 0] - radii[i])
            cons.append(1.0 - (centers[i, 0] + radii[i]))
            cons.append(centers[i, 1] - radii[i])
            cons.append(1.0 - (centers[i, 1] + radii[i]))
        # No overlap
        for i in range(num_circles):
            for j in range(i + 1, num_circles):
                dist = np.linalg.norm(centers[i] - centers[j])
                cons.append(dist - (radii[i] + radii[j]))
        return np.array(cons)

    def try_grow_radii(params):
        """Try to uniformly scale up radii while maintaining feasibility"""
        centers, radii = get_centers_radii(params)

        # Binary search for maximum scale factor
        low, high = 1.0, 2.0
        best_params = params.copy()

        for _ in range(50):
            mid = (low + high) / 2
            new_radii = radii * mid
            new_params = np.concatenate([centers.flatten(), new_radii])
            if check_feasibility(new_params, tol=1e-9):
                low = mid
                best_params = new_params.copy()
            else:
                high = mid

        return best_params

    def run_slsqp(params, maxiter=1000, ftol=1e-12):
        res = minimize(
            get_fitness,
            params,
            method='SLSQP',
            constraints={'type': 'ineq', 'fun': constraints_ineq},
            options={'maxiter': maxiter, 'ftol': ftol, 'disp': False}
        )
        return res.x

    def run_cobyla(params, maxiter=5000):
        constraints_list = []
        for i in range(num_circles):
            constraints_list.append({'type': 'ineq', 'fun': lambda p, i=i: p[2*i] - p[2*num_circles + i]})
            constraints_list.append({'type': 'ineq', 'fun': lambda p, i=i: 1.0 - (p[2*i] + p[2*num_circles + i])})
            constraints_list.append({'type': 'ineq', 'fun': lambda p, i=i: p[2*i+1] - p[2*num_circles + i]})
            constraints_list.append({'type': 'ineq', 'fun': lambda p, i=i: 1.0 - (p[2*i+1] + p[2*num_circles + i])})

        for i in range(num_circles):
            for j in range(i+1, num_circles):
                constraints_list.append({
                    'type': 'ineq',
                    'fun': lambda p, i=i, j=j: np.sqrt((p[2*i]-p[2*j])**2 + (p[2*i+1]-p[2*j+1])**2) - p[2*num_circles+i] - p[2*num_circles+j]
                })

        res = minimize(
            get_fitness,
            params,
            method='COBYLA',
            constraints=constraints_list,
            options={'maxiter': maxiter, 'rhobeg': 0.01, 'disp': False}
        )
        return res.x

    def run_trust_constr(params, maxiter=5000):
        def constraint_func(p):
            return constraints_ineq(p)

        n_constraints = 4 * num_circles + num_circles * (num_circles - 1) // 2
        nlc = NonlinearConstraint(constraint_func, 0, np.inf)

        bounds = [(0, 1)] * (2 * num_circles) + [(0, 0.5)] * num_circles

        res = minimize(
            get_fitness,
            params,
            method='trust-constr',
            constraints=nlc,
            bounds=bounds,
            options={'maxiter': maxiter, 'disp': False}
        )
        return res.x

    # Initialize with a good starting configuration
    # Use hexagonal-like packing
    def create_hex_init():
        centers = []
        r_est = 0.1
        y = r_est
        row = 0
