import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n = 26

    def pack_to_vector(centers, radii):
        return np.concatenate([centers.flatten(), radii])

    def vector_to_pack(x):
        centers = x[:2*n].reshape(n, 2)
        radii = x[2*n:]
        return centers, radii

    def objective(x):
        radii = x[2*n:]
        return -np.sum(radii)

    def constraint_inside(x):
        centers, radii = vector_to_pack(x)
        constraints = []
        for i in range(n):
            cx, cy = centers[i]
            r = radii[i]
            constraints.append(cx - r)
            constraints.append(cy - r)
            constraints.append(1 - cx - r)
            constraints.append(1 - cy - r)
        return np.array(constraints)

    def constraint_no_overlap(x):
        centers, radii = vector_to_pack(x)
        constraints = []
        for i in range(n):
            for j in range(i+1, n):
                dist = np.sqrt((centers[i,0] - centers[j,0])**2 + 
                              (centers[i,1] - centers[j,1])**2)
                constraints.append(dist - radii[i] - radii[j])
        return np.array(constraints)

    def all_constraints(x):
        return np.concatenate([constraint_inside(x), constraint_no_overlap(x)])

    def get_initial_config(spacing, offset_x=0, offset_y=0, seed=0):
        np.random.seed(seed)
        positions = []

        # Different configurations based on spacing
        if spacing < 0.17:
            # 6 rows configuration
            rows = 6
            cols = [4, 5, 4, 5, 4, 4]  # 26 total
            y_positions = [0.08, 0.24, 0.40, 0.56, 0.72, 0.88]
        elif spacing < 0.19:
            # 5 rows with offset pattern
            rows = 5
            cols = [5, 5, 6, 5, 5]  # 26 total
            y_positions = [0.1, 0.28, 0.46, 0.64, 0.82]
        else:
            # Standard 5x5 + 1
            rows = 5
            cols = [5, 5, 5, 5, 5]
            y_positions = [0.1, 0.28, 0.46, 0.64, 0.82]

        for row_idx, (y, num_cols) in enumerate(zip(y_positions, cols)):
            x_spacing = 0.9 / (num_cols + 1)
            x_offset = 0.05 + (row_idx % 2) * x_spacing * 0.3 * offset_x
            for i in range(num_cols):
                x = x_offset + x_spacing * (i + 1)
                positions.append((x + np.random.randn() * 0.01, 
                                 y + offset_y + np.random.randn() * 0.01))

        # Add remaining circles if needed
        while len(positions) < n:
            positions.append((0.5 + np.random.randn() * 0.1, 
                             0.5 + np.random.randn() * 0.1))

        positions = positions[:n]

        centers = np.array(positions)
        centers = np.clip(centers, 0.05, 0.95)

        base_r = 0.07
        radii = base_r * (0.9 + 0.2 * np.random.random(n))

        return centers, radii

    def coordinate_descent(centers, radii, iterations=100):
        """Fine-tune using coordinate descent"""
        for _ in range(iterations):
            improved = False
            for i in range(n):
                # Try to increase radius
                max_r = min(centers[i,0], centers[i,1], 
                           1 - centers[i,0], 1 - centers[i,1])
                for j in range(n):
                    if i != j:
                        dist = np.sqrt((centers[i,0] - centers[j,0])**2 + 
                                      (centers[i,1] - centers[j,1])**2)
                        max_r = min(max_r, dist - radii[j])

                if max_r > radii[i] + 1e-9:
                    radii[i] = max_r - 1e-9
                    improved = True

                # Try to move center to allow larger radius
                for dx, dy in [(0.001, 0), (-0.001, 0), (0, 0.001), (0, -0.001)]:
                    new_cx = centers[i,0] + dx
                    new_cy = centers[i,1] + dy

                    if new_cx < 0.01 or new_cx > 0.99 or new_cy < 0.01 or new_cy > 0.99:
                        continue

                    new_max_r = min(new_cx, new_cy, 1 - new_cx, 1 - new_cy)
                    valid = True
                    for j in range(n):
                        if i != j:
                            dist = np.sqrt((new_cx - centers[j,0])**2 + 
                                          (new_cy - centers[j,1])**2)
                            if dist < radii[j] + 1e-9:
                                valid = False
                                break
                            new_max_r = min(new_max_r, dist - radii[j])

                    if valid and new_max_r > radii[i] + 1e-9:
                        centers[i,0] = new_cx
                        centers[i,1] = new_cy
                        radii[i] = new_max_r - 1e-9
                        improved = True

            if not improved:
                break

        return centers, radii

    best_sum = 0
    best_centers = None
    best_radii = None

    bounds = []
    for i in range(n):
        bounds.append((0.01, 0.99))
        bounds.append((0.01, 0.99))
    for i in range(n):
        bounds.append((0.001, 0.5))

    constraints = {'type': 'ineq', 'fun': all_constraints}

    # Try different spacings and configurations
    spacings = [0.16, 0.17, 0.18, 0.19, 0.20]

    for trial in range(40):
        spacing = spacings[trial % len(spacings)]
        offset_x = (trial // 5) % 3 * 0.3
        offset_y = ((trial // 15) % 3 - 1) * 0.02

        centers, radii = get_initial_config(spacing, offset_x, offset_y, seed=trial * 42)

        x0 = pack_to_vector(centers, radii)

        result = minimize(
            objective, x0, method='SLSQP',
            bounds=bounds, constraints=constraints,
            options={'maxiter': 1000, 'ftol': 1e-10}
        )

        centers_opt, radii_opt = vector_to_pack(result.x)

        # Apply coordinate descent
        centers_opt, radii_opt = coordinate_descent(centers_opt, radii_opt, iterations=200)

        # Verify constraints
        x_check = pack_to_vector(centers_opt, radii_opt)
        inside_ok = np.all(constraint_inside(x_check) >= -1e-6)
        overlap_ok = np.all(constraint_no_overlap(x_check) >= -1e-6)

        if inside_ok and overlap_ok:
            sum_r = np.sum(radii_opt)
            if sum_r > best_sum:
                best_sum = sum_r
                best_centers = centers_opt.copy()
                best_radii = radii_opt.copy()

    # Refined optimization from best solution
    if best_centers is not None:
        for refine in range(10):
            x0 = pack_to_vector(best_centers, best_radii)

            result = minimize(
                objective, x0, method='SLSQP',
                bounds=bounds, constraints=constraints,
                options={'maxiter': 2000, 'ftol': 1e-12}
            )

            centers_opt, radii_opt = vector_to_pack(result.x)
            centers_opt, radii_opt = coordinate_descent(centers_opt, radii_opt, iterations=500)

            sum_r = np.sum(radii_opt)
            if sum_r > best_sum:
                best_sum = sum_r
                best_centers = centers_opt.copy()
                best_radii = radii_opt.copy()

    # Final strict feasibility check
    for i in range(n):
        best_radii[i] = min(best_radii[i], 
                           best_centers[i,0], 
                           best_centers[i,1],
                           1 - best_centers[i,0], 
                           1 - best_centers[i,1])

    for i in range(n):
        for j in range(i+1, n):
            dist = np.sqrt((best_centers[i,0] - best_centers[j,0])**2 + 
                          (best_centers[i,1] - best_centers[j,1])**2)
            if dist < best_radii[i] + best_radii[j] + 1e-10:
                factor = (dist - 1e-10) / (best_radii[i] + best_radii[j])
                best_radii[i] *= factor
                best_radii[j] *= factor

    best_radii = np.maximum(best_radii, 0)

    return best_centers, best_radii, np.sum(best_radii)


def run_packing():
    return construct_packing()


if __name__ == "__main__":
    centers, radii, sum_radii = run_packing()
    print(f"Sum of radii: {sum_radii}")
    print(f"Target: 2.636")

