import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n = 26
    best_sum = 0
    best_centers = None
    best_radii = None

    # Multiple initial configurations
    def get_initial_configs():
        configs = []

        # Config 1: 5x5 grid + 1 center
        grid_size = 5
        x = np.linspace(0.1, 0.9, grid_size)
        y = np.linspace(0.1, 0.9, grid_size)
        xv, yv = np.meshgrid(x, y)
        centers1 = np.vstack([xv.ravel(), yv.ravel()]).T
        centers1 = np.vstack([centers1, [0.5, 0.5]])
        configs.append(centers1)

        # Config 2: Hexagonal-like packing
        centers2 = []
        row = 0
        while len(centers2) < n:
            x_start = 0.1 if row % 2 == 0 else 0.18
            for x in np.arange(x_start, 0.95, 0.16):
                if len(centers2) < n:
                    centers2.append([x, 0.1 + row * 0.14])
            row += 1
        configs.append(np.array(centers2[:n]))

        # Config 3: Different hex spacing
        centers3 = []
        row = 0
        spacing = 0.18
        while len(centers3) < n:
            offset = (row % 2) * spacing / 2
            for x in np.arange(0.1 + offset, 0.95, spacing):
                if len(centers3) < n:
                    centers3.append([x, 0.1 + row * spacing * 0.866])
            row += 1
        configs.append(np.array(centers3[:n]))

        # Config 4: 6x5 grid minus 4
        x = np.linspace(0.1, 0.9, 6)
        y = np.linspace(0.1, 0.9, 5)
        xv, yv = np.meshgrid(x, y)
        centers4 = np.vstack([xv.ravel(), yv.ravel()]).T[:n]
        configs.append(centers4)

        # Config 5: Row-based with varying sizes
        centers5 = []
        for i in range(5):
            for j in range(5):
                centers5.append([0.1 + i * 0.2, 0.1 + j * 0.2])
        centers5.append([0.5, 0.5])
        configs.append(np.array(centers5))

        return configs

    def objective(params):
        return -np.sum(params[2*n:])

    def constraints(params):
        pts = params[:2*n].reshape((n, 2))
        r = params[2*n:]
        cons = []

        for i in range(n):
            cons.append(pts[i, 0] - r[i])
            cons.append(1.0 - pts[i, 0] - r[i])
            cons.append(pts[i, 1] - r[i])
            cons.append(1.0 - pts[i, 1] - r[i])

        for i in range(n):
            for j in range(i + 1, n):
                dist = np.sqrt(np.sum((pts[i] - pts[j])**2))
                cons.append(dist - (r[i] + r[j]))
        return np.array(cons)

    bounds = [(0.001, 0.999) for _ in range(2*n)] + [(0.001, 0.5) for _ in range(n)]

    initial_configs = get_initial_configs()

    for config_idx, centers in enumerate(initial_configs):
        for seed in range(8):
            np.random.seed(seed + config_idx * 20)
            perturbed = centers.copy() + np.random.normal(0, 0.02, centers.shape)
            perturbed = np.clip(perturbed, 0.05, 0.95)

            r_init = np.full(n, 0.08)
            initial_params = np.concatenate([perturbed.flatten(), r_init])

            try:
                res = minimize(objective, initial_params, method='SLSQP',
                             bounds=bounds, constraints={'type': 'ineq', 'fun': constraints},
                             options={'maxiter': 1000, 'ftol': 1e-10})

                curr_centers = res.x[:2*n].reshape((n, 2))
                curr_radii = res.x[2*n:].copy()

                # Cleanup constraints
                for i in range(n):
                    curr_radii[i] = min(curr_radii[i], curr_centers[i,0], 1-curr_centers[i,0],
                                       curr_centers[i,1], 1-curr_centers[i,1])
                    curr_radii[i] = max(0, curr_radii[i])

                for _ in range(3):
                    for i in range(n):
                        for j in range(i+1, n):
                            d = np.linalg.norm(curr_centers[i] - curr_centers[j])
                            if curr_radii[i] + curr_radii[j] > d + 1e-10:
                                factor = (d - 1e-10) / (curr_radii[i] + curr_radii[j])
                                curr_radii[i] *= factor
                                curr_radii[j] *= factor

                if np.sum(curr_radii) > best_sum:
                    best_sum = np.sum(curr_radii)
                    best_centers = curr_centers.copy()
                    best_radii = curr_radii.copy()
            except:
                continue

    # Coordinate descent refinement
    if best_centers is not None:
        for _ in range(100):
            improved = False
            for i in range(n):
                for dx, dy in [(0.01,0),(-0.01,0),(0,0.01),(0,-0.01),
                               (0.005,0.005),(-0.005,0.005),(0.005,-0.005),(-0.005,-0.005),
                               (0.002,0),(0,-0.002),(0.002,0.002)]:
                    new_center = best_centers[i] + np.array([dx, dy])
                    if not (0.01 < new_center[0] < 0.99 and 0.01 < new_center[1] < 0.99):
                        continue

                    max_r = min(new_center[0], 1-new_center[0], new_center[1], 1-new_center[1])
                    for j in range(n):
                        if i != j:
                            max_r = min(max_r, np.linalg.norm(new_center - best_centers[j]) - best_radii[j])

                    if max_r > best_radii[i] + 1e-8:
                        best_centers[i] = new_center
                        best_radii[i] = max_r
                        improved = True
            if not improved:
                break

    return best_centers, best_radii, np.sum(best_radii)

def run_packing():
    return construct_packing()

