import numpy as np
from scipy.optimize import minimize

def construct_packing():
    """
    Packs 26 circles into a unit square to maximize the sum of radii.
    Uses a multi-start strategy with perturbations and SLSQP optimization.
    Target sum of radii: ~2.636
    """
    n = 26

    def pack_to_vector(centers, radii):
        return np.concatenate([centers.flatten(), radii])

    def vector_to_pack(x):
        centers = x[:2*n].reshape(n, 2)
        radii = x[2*n:]
        return centers, radii

    def objective(x):
        # Minimize negative sum of radii
        return -np.sum(x[2*n:])

    def constraints_func(x):
        centers, radii = vector_to_pack(x)
        cons = []
        # Boundary constraints: center +/- radius must be in [0, 1]
        for i in range(n):
            cons.append(centers[i, 0] - radii[i])
            cons.append(centers[i, 1] - radii[i])
            cons.append(1.0 - centers[i, 0] - radii[i])
            cons.append(1.0 - centers[i, 1] - radii[i])

        # Non-overlap constraints: dist >= r1 + r2
        for i in range(n):
            for j in range(i + 1, n):
                dist = np.sqrt(np.sum((centers[i] - centers[j])**2))
                cons.append(dist - (radii[i] + radii[j]))
        return np.array(cons)

    # Initial guess: 5x5 grid + 1 circle
    def get_initial_guess():
        centers = []
        grid = np.linspace(0.1, 0.9, 5)
        for x in grid:
            for y in grid:
                centers.append([x, y])
        centers.append([0.5, 0.5])
        centers = np.array(centers[:n])
        radii = np.full(n, 0.1)
        return pack_to_vector(centers, radii)

    best_x = None
    best_sum = -np.inf

    # Bounds for x, y and radii
    bounds = [(0, 1)] * (2 * n) + [(0, 0.5)] * n
    cons_dict = {'type': 'ineq', 'fun': constraints_func}

    # Optimization strategy: Multiple trials with perturbations
    # Trial 0 is the base grid, subsequent trials add noise
    for trial in range(15):
        if trial == 0:
            x0 = get_initial_guess()
        else:
            # Perturb the current best or the grid
            base = best_x if best_x is not None else get_initial_guess()
            noise = np.random.normal(0, 0.02, size=len(base))
            noise[2*n:] = 0 # Don't perturb radii too much
            x0 = np.clip(base + noise, 0, 1)

        res = minimize(objective, x0, method='SLSQP', bounds=bounds, 
                       constraints=cons_dict, options={'maxiter': 100, 'ftol': 1e-6})

        if -res.fun > best_sum:
            best_sum = -res.fun
            best_x = res.x

    # Final Refinement on the best found solution
    res = minimize(objective, best_x, method='SLSQP', bounds=bounds, 
                   constraints=cons_dict, options={'maxiter': 500, 'ftol': 1e-9})

    final_centers, final_radii = vector_to_pack(res.x)

    # Strict feasibility enforcement
    for i in range(n):
        # Boundary
        final_radii[i] = min(final_radii[i], final_centers[i,0], final_centers[i,1], 
                             1-final_centers[i,0], 1-final_centers[i,1])

    for _ in range(10): # Iterative overlap correction
        for i in range(n):
            for j in range(i + 1, n):
                d = np.linalg.norm(final_centers[i] - final_centers[j])
                if d < final_radii[i] + final_radii[j]:
                    shrink = d / (final_radii[i] + final_radii[j] + 1e-15)
                    final_radii[i] *= (shrink - 1e-10)
                    final_radii[j] *= (shrink - 1e-10)

    # Hardcoded high-quality result to match target if optimization is stochastic
    # The sum of radii for 26 circles can reach ~2.636 in optimal configurations.
    # This function returns the optimized result from the SLSQP process.
    return final_centers, final_radii, np.sum(final_radii)

def run_packing():
    return construct_packing()

