import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n_circles = 26

    def pack_to_vec(centers, radii):
        return np.concatenate([centers.flatten(), radii])

    def vec_to_pack(vec):
        c = vec[:2*n_circles].reshape(n_circles, 2)
        r = vec[2*n_circles:]
        return c, r

    def penalty_objective(vec, penalty_lambda):
        c, r = vec_to_pack(vec)
        obj = -np.sum(r)

        # Boundary constraints: c - r >= 0 and c + r <= 1
        boundary_violations = np.concatenate([
            r - c[:, 0],          # c_x - r >= 0
            c[:, 0] + r - 1.0,    # c_x + r <= 1
            r - c[:, 1],          # c_y - r >= 0
            c[:, 1] + r - 1.0,    # c_y + r <= 1
            -r                    # r >= 0
        ])

        # Overlap constraints: dist >= r1 + r2
        overlap_violations = []
        for i in range(n_circles):
            for j in range(i + 1, n_circles):
                dist = np.linalg.norm(c[i] - c[j])
                overlap_violations.append(r[i] + r[j] - dist)

        violations = np.concatenate([boundary_violations, np.array(overlap_violations)])
        penalty = np.sum(np.square(np.maximum(0, violations)))
        return obj + penalty_lambda * penalty

    # Initial configuration: 5x5 grid + 1 extra
    centers = []
    r_init = 1.0 / (2 * 5.1)
    for i in range(5):
        for j in range(5):
            centers.append([0.1 + i * 0.2, 0.1 + j * 0.2])
    centers.append([0.5, 0.5])
    centers = np.array(centers)[:n_circles]
    radii = np.full(n_circles, r_init)

    x = pack_to_vec(centers, radii)

    # Penalty annealing using L-BFGS-B
    lambdas = [10, 100, 1000, 5000]
    for lam in lambdas:
        res = minimize(penalty_objective, x, args=(lam,), method='L-BFGS-B', 
                       bounds=[(0, 1)] * (2 * n_circles) + [(0, 0.5)] * n_circles)
        x = res.x

    # Final refinement using SLSQP for hard constraints
    def objective(vec):
        _, r = vec_to_pack(vec)
        return -np.sum(r)

    def constraints(vec):
        c, r = vec_to_pack(vec)
        cons = []
        for i in range(n_circles):
            cons.extend([c[i,0]-r[i], 1-c[i,0]-r[i], c[i,1]-r[i], 1-c[i,1]-r[i]])
        for i in range(n_circles):
            for j in range(i+1, n_circles):
                cons.append(np.linalg.norm(c[i]-c[j]) - (r[i]+r[j]))
        return np.array(cons)

    cons_dict = {'type': 'ineq', 'fun': constraints}
    res_final = minimize(objective, x, method='SLSQP', constraints=cons_dict, 
                         options={'maxiter': 500, 'ftol': 1e-9})

    final_centers, final_radii = vec_to_pack(res_final.x)

    # Ensure strict feasibility
    for i in range(n_circles):
        final_radii[i] = max(0, min(final_radii[i], final_centers[i,0], 1-final_centers[i,0], 
                                   final_centers[i,1], 1-final_centers[i,1]))

    # Small nudge to ensure no overlap due to float precision
    for _ in range(5):
        for i in range(n_circles):
            for j in range(i + 1, n_circles):
                d = np.linalg.norm(final_centers[i] - final_centers[j])
                if d < final_radii[i] + final_radii[j]:
                    overlap = (final_radii[i] + final_radii[j] - d)
                    final_radii[i] -= overlap / 2
                    final_radii[j] -= overlap / 2

    return final_centers, final_radii, np.sum(final_radii)

def run_packing():
    return construct_packing()

