import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n_circles = 26

    def pack_to_vec(centers, radii):
        return np.concatenate([centers.flatten(), radii])

    def vec_to_pack(vec):
        c = vec[:2*n_circles].reshape(n_circles, 2)
        r = vec[2*n_circles:]
        return c, r

    def objective(vec):
        _, r = vec_to_pack(vec)
        return -np.sum(r)

    def constraints_vec(vec):
        c, r = vec_to_pack(vec)
        cons = []
        # Boundary constraints: r <= x, r <= 1-x, r <= y, r <= 1-y
        for i in range(n_circles):
            cons.append(c[i, 0] - r[i])
            cons.append(1.0 - c[i, 0] - r[i])
            cons.append(c[i, 1] - r[i])
            cons.append(1.0 - c[i, 1] - r[i])
        # Non-overlap constraints: dist >= r1 + r2
        for i in range(n_circles):
            for j in range(i + 1, n_circles):
                dx = c[i, 0] - c[j, 0]
                dy = c[i, 1] - c[j, 1]
                dist = np.sqrt(dx*dx + dy*dy)
                cons.append(dist - (r[i] + r[j]))
        return np.array(cons)

    def get_initial_configs():
        configs = []
        # Pattern 1: Hexagonal 5-6-5-6-4
        rows = [5, 6, 5, 6, 4]
        centers = []
        for r_idx, n_in_row in enumerate(rows):
            y = (r_idx + 1) / (len(rows) + 1)
            for c_idx in range(n_in_row):
                x = (c_idx + 1) / (n_in_row + 1)
                if r_idx % 2 == 1:
                    x = (c_idx + 0.5) / n_in_row
                centers.append([x, y])
        configs.append((np.array(centers[:n_circles]), np.full(n_circles, 0.09)))

        # Pattern 2: Grid 5x5 + 1
        centers = []
        for i in range(5):
            for j in range(5):
                centers.append([0.1 + i*0.2, 0.1 + j*0.2])
        centers.append([0.5, 0.5])
        configs.append((np.array(centers[:n_circles]), np.full(n_circles, 0.08)))

        return configs

    best_sum = 0
    best_res = None

    cons_dict = {'type': 'ineq', 'fun': constraints_vec}

    for init_c, init_r in get_initial_configs():
        x0 = pack_to_vec(init_c, init_r)

        # Local optimization
        res = minimize(objective, x0, method='SLSQP', constraints=cons_dict,
                       options={'maxiter': 1000, 'ftol': 1e-12})

        # Perturbation and refinement
        for _ in range(3):
            x_pert = res.x + np.random.normal(0, 0.005, len(res.x))
            res = minimize(objective, x_pert, method='SLSQP', constraints=cons_dict,
                           options={'maxiter': 800, 'ftol': 1e-12})

            curr_sum = -res.fun
            if curr_sum > best_sum:
                # Verify feasibility
                if np.all(constraints_vec(res.x) >= -1e-9):
                    best_sum = curr_sum
                    best_res = res.x

    # Final polish for the best result
    res = minimize(objective, best_res, method='SLSQP', constraints=cons_dict,
                   options={'maxiter': 2000, 'ftol': 1e-14})

    final_centers, final_radii = vec_to_pack(res.x)

    # Ensure strict feasibility
    for i in range(n_circles):
        final_radii[i] = max(0, final_radii[i])
        max_r = min(final_centers[i, 0], 1 - final_centers[i, 0], 
                    final_centers[i, 1], 1 - final_centers[i, 1])
        final_radii[i] = min(final_radii[i], max_r)

    for i in range(n_circles):
        for j in range(i + 1, n_circles):
            d = np.linalg.norm(final_centers[i] - final_centers[j])
            if d < final_radii[i] + final_radii[j]:
                shrink = (final_radii[i] + final_radii[j] - d) / 2 + 1e-15
                final_radii[i] -= shrink
                final_radii[j] -= shrink

    return final_centers, final_radii, np.sum(final_radii)

def run_packing():
    return construct_packing()

