from scipy.optimize import minimize

def construct_packing():
    n_circles = 26

    def pack_to_vec(centers, radii):
        return np.concatenate([centers.flatten(), radii])

    def vec_to_pack(vec):
        c = vec[:2*n_circles].reshape(n_circles, 2)
        r = vec[2*n_circles:]
        return c, r

    def objective(vec):
        _, r = vec_to_pack(vec)
        return -np.sum(r)

    def constraints_vec(vec):
        c, r = vec_to_pack(vec)
        cons = []
        # Boundary constraints: r_i <= x_i <= 1-r_i
        for i in range(n_circles):
            cons.append(c[i,0] - r[i])
            cons.append(1.0 - c[i,0] - r[i])
            cons.append(c[i,1] - r[i])
            cons.append(1.0 - c[i,1] - r[i])
        # Non-overlap constraints: dist(i,j) >= r_i + r_j
        for i in range(n_circles):
            for j in range(i + 1, n_circles):
                dist = np.linalg.norm(c[i] - c[j])
                cons.append(dist - (r[i] + r[j]))
        return np.array(cons)

    def create_initial_guess():
        # A dense staggered pattern for 26 circles
        rows = [5, 5, 6, 5, 5]
        centers = []
        y_coords = np.linspace(0.1, 0.9, len(rows))
        for row_idx, n_in_row in enumerate(rows):
            x_coords = np.linspace(0.1, 0.9, n_in_row)
            # Offset every other row
            if row_idx % 2 == 1:
                x_coords += 0.05
            for x in x_coords:
                centers.append([x, y_coords[row_idx]])

        centers = np.array(centers[:n_circles])
        radii = np.full(n_circles, 1.0 / (2 * np.sqrt(n_circles)))
        return pack_to_vec(centers, radii)

    # Optimization bounds
    bounds = []
    for _ in range(n_circles * 2):
        bounds.append((0, 1))  # Centers
    for _ in range(n_circles):
        bounds.append((0, 0.5)) # Radii

    x0 = create_initial_guess()
    cons = {'type': 'ineq', 'fun': constraints_vec}

    # Primary optimization using SLSQP
    res = minimize(
        objective, 
        x0, 
        method='SLSQP', 
        bounds=bounds, 
        constraints=cons,
        options={'maxiter': 1000, 'ftol': 1e-12}
    )

    # Refinement step with tighter tolerance
    res = minimize(
        objective, 
        res.x, 
        method='SLSQP', 
        bounds=bounds, 
        constraints=cons,
        options={'maxiter': 1000, 'ftol': 1e-15}
    )

    final_centers, final_radii = vec_to_pack(res.x)

    # Final validity check and minor adjustment to ensure hard constraints
    # (Subtracting a tiny epsilon from radii if any constraint is slightly violated)
    violations = constraints_vec(res.x)
    if np.any(violations < 0):
        final_radii -= np.abs(np.min(violations))

    return final_centers, final_radii, np.sum(final_radii)

def run_packing():
    return construct_packing()

if __name__ == "__main__":
    centers, radii, total_r = run_packing()

