            for i in range(min(n, len(grid_pos))):
                centers.append(grid_pos[i])
                radii.append(r_val)
            while len(centers) < n:
                centers.append([0.5 + np.random.randn()*0.1, 0.5 + np.random.randn()*0.1])
                radii.append(0.05)

        else:
            # Random with varying sizes
            np.random.seed(config_type)
            for _ in range(n):
                centers.append([np.random.uniform(0.15, 0.85), np.random.uniform(0.15, 0.85)])
                radii.append(np.random.uniform(0.06, 0.12))

        return np.array(centers[:n]), np.array(radii[:n])

    best_result = None
    best_sum = -np.inf

    for config_type in range(15):
        init_centers, init_radii = generate_initial_config(config_type % 4)

        x0 = []
        for i in range(n):
            x0.extend([init_centers[i][0], init_centers[i][1], init_radii[i]])
        x0 = np.array(x0)

        for trial in range(3):
            if trial > 0:
                x_init = x0 + np.random.randn(len(x0)) * 0.03
                x_init = np.clip(x_init, [b[0] for b in bounds], [b[1] for b in bounds])
            else:
                x_init = x0.copy()

            try:
                result = minimize(objective, x_init, method='SLSQP', bounds=bounds,
                                constraints=cons, jac=objective_grad,
                                options={'maxiter': 5000, 'ftol': 1e-12})

