import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n = 26

    def unpack(x):
        centers = x.reshape(n, 3)[:, :2]
        radii = x.reshape(n, 3)[:, 2]
        return centers, radii

    def objective(x):
        _, radii = unpack(x)
        return -np.sum(radii)

    def constraint_func(x):
        centers, radii = unpack(x)
        constraints = []

        for i in range(n):
            for j in range(i+1, n):
                dist = np.sqrt((centers[i,0]-centers[j,0])**2 + (centers[i,1]-centers[j,1])**2)
                constraints.append(dist - radii[i] - radii[j])

        for i in range(n):
            constraints.append(centers[i,0] - radii[i])
            constraints.append(centers[i,1] - radii[i])
            constraints.append(1 - centers[i,0] - radii[i])
            constraints.append(1 - centers[i,1] - radii[i])

        return np.array(constraints)

    def create_initial_config(variant=0):
        centers = []
        radii = []

        if variant == 0:
            # Hexagonal-inspired packing
            r_base = 0.105
            rows = [4, 5, 5, 5, 4, 3]
            y_positions = [0.1, 0.28, 0.46, 0.64, 0.82, 0.95]

            idx = 0
            for row_idx, (num_in_row, y) in enumerate(zip(rows, y_positions)):
                if idx >= n:
                    break
                x_start = 0.5 - (num_in_row - 1) * 0.11
                for j in range(num_in_row):
                    if idx >= n:
                        break
                    x = x_start + j * 0.22
                    r = r_base * (1.1 if (y < 0.15 or y > 0.85) else 1.0)
                    centers.append([np.clip(x, 0.1, 0.9), y])
                    radii.append(r)
                    idx += 1
        else:
            # Grid with size variation
            np.random.seed(variant)
            grid_size = 6
            positions = []
            for i in range(grid_size):
                for j in range(grid_size):
                    x = (i + 0.5) / grid_size
                    y = (j + 0.5) / grid_size
                    positions.append([x, y])

            np.random.shuffle(positions)
            centers = positions[:n]
            radii = [0.08 + 0.04 * np.random.random() for _ in range(n)]

        while len(centers) < n:
            centers.append([np.random.random(), np.random.random()])
            radii.append(0.05)

        return centers[:n], radii[:n]

    bounds = []
    for i in range(n):
        bounds.extend([(0.01, 0.99), (0.01, 0.99), (0.001, 0.5)])

    cons = {'type': 'ineq', 'fun': constraint_func}

    best_result = None
    best_sum = -np.inf

    for attempt in range(25):
        init_centers, init_radii = create_initial_config(attempt % 5)

        x0 = []
        for i in range(n):
            x0.extend([init_centers[i][0], init_centers[i][1], init_radii[i]])
        x0 = np.array(x0)

        if attempt >= 5:
            x0 += np.random.randn(len(x0)) * (0.01 + 0.01 * (attempt // 5))
            x0 = np.clip(x0, [b[0] for b in bounds], [b[1] for b in bounds])

        try:
            result = minimize(objective, x0, method='SLSQP', bounds=bounds,
                            constraints=cons, options={'maxiter': 5000, 'ftol': 1e-12})

            centers, radii = unpack(result.x)

            # Grow radii individually
            for _ in range(3):
                for i in range(n):
                    max_r = min(centers[i,0], centers[i,1], 1-centers[i,0], 1-centers[i,1])
                    for j in range(n):
                        if i != j:
                            dist = np.sqrt((centers[i,0]-centers[j,0])**2 + (centers[i,1]-centers[j,1])**2)
                            max_r = min(max_r, dist - radii[j])
                    if max_r > radii[i]:
                        radii[i] = max_r * 0.9999

            current_sum = np.sum(radii)

            if current_sum > best_sum:
                valid = True
                eps = 1e-6
                for i in range(n):
                    if radii[i] < -eps or centers[i,0]-radii[i] < -eps or centers[i,1]-radii[i] < -eps:
                        valid = False
                    if centers[i,0]+radii[i] > 1+eps or centers[i,1]+radii[i] > 1+eps:
                        valid = False
                for i in range(n):
                    for j in range(i+1, n):
                        dist = np.sqrt((centers[i,0]-centers[j,0])**2 + (centers[i,1]-centers[j,1])**2)
                        if dist < radii[i] + radii[j] - eps:
                            valid = False

                if valid:
                    best_sum = current_sum
                    best_result = np.zeros(3*n)
                    for i in range(n):
                        best_result[3*i] = centers[i,0]
                        best_result[3*i+1] = centers[i,1]
                        best_result[3*i+2] = radii[i]
        except:
            continue

    if best_result is not None:
        centers, radii = unpack(best_result)
    else:
        centers, radii = create_initial_config(0)
        centers = np.array(centers)
        radii = np.array(radii)

    radii = np.maximum(radii, 0)
    for i in range(n):
        max_r = min(centers[i,0], centers[i,1], 1-centers[i,0], 1-centers[i,1])
        radii[i] = min(radii[i], max_r * 0.9999)

    return centers, radii, np.sum(radii)

def run_packing():
    return construct_packing()

